import React from 'react';
import { Card } from 'primereact/card';
import { FaCalendarAlt, FaMapMarkerAlt, FaBook } from 'react-icons/fa';

const ExperienceCard = ({ logo, title, position, duration, location, courses }) => {
    const footerTemplate = (
        <div>
            <div className="p-d-flex p-flex-column">
                <small className="p-mb-2">
                    <FaCalendarAlt className="p-mr-1" /> {duration}
                </small>
                {location && (
                    <small>
                        <FaMapMarkerAlt className="p-mr-1" /> {location}
                    </small>
                )}
            </div>
        </div>
    );

    return (
        <Card title={title} subTitle={position} footer={footerTemplate} className="p-shadow-6">
            <div>
                <h5 className="p-mb-2">
                    <FaBook className="p-mr-1" /> Cours:
                </h5>
                <ul className="p-pl-3">
                    {courses.map((course, index) => (
                        <li key={index}>{course}</li>
                    ))}
                </ul>
            </div>
        </Card>
    );
};

const Experiences = () => {
    // Données pour chaque expérience
    const experienceData = [
        {
            logo: '../assets/ensaj.png',
            title: 'Ecole Nationale des Sciences Appliquées d\'El Jadida',
            position: 'Professeur habilité',
            duration: 'juin 2023 - aujourd\'hui',
            location: 'Province d’El Jadida, Morocco',
            courses: [
                'Développement JEE',
                'Programmation réseau et JEE',
                'Programmation mobile',
                'Programmation web',
                'Programmation multiplateforme'
            ]
        },
        {
            logo: '../assets/ensaj.png',
            title: 'Ecole Nationale des Sciences Appliquées d\'El Jadida',
            position: 'Professeur d\'enseignement supérieur assistant',
            duration: 'juin 2019 - juin 2023',
            location: 'Province d’El Jadida, Morocco',
            courses: [
                'Programmation réseau et JEE',
                'Programmation mobile',
                'Programmation web',
                'Programmation multiplateforme'
            ]
        },
        {
            logo: 'https://chemin/vers/logo/emsie.png',
            title: 'Ecole Marocaine des Sciences de l\'ingénieur',
            position: 'Professeur (Temps partiel)',
            duration: 'sept. 2011 - aujourd\'hui',
            location: 'Temps partiel',
            courses: [
                'Java et Java avancé',
                'JEE',
                'Architecture des applications d\'entreprises',
                'Programmation mobile'
            ]
        }
    ];

    return (
        <section className="section">
            <div className="experience-grid p-d-flex p-flex-wrap">
                {experienceData.map((experience, index) => (
                    <div key={index} className="p-mr-2 p-mb-2" style={{ maxWidth: '400px' }}>
                        <ExperienceCard
                            title={experience.title}
                            position={experience.position}
                            duration={experience.duration}
                            location={experience.location}
                            courses={experience.courses}
                        />
                    </div>
                ))}
            </div>
        </section>
    );
};


export default Experiences;
