import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Pagination from "react-bootstrap/Pagination";
import download from "../assets/download.png";


function SkillCard(props) {
  const { logo, title, issuer, date, id } = props;
  return (
    <Card className="shadow bg-light mb-4 animated bounceIn">
      <Card.Img className="Skill-image" variant="top" src={logo} />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>
          <span>{issuer}</span>
          <br />
          <span>Date de d√©livrance: {date}</span>
          <br />
          <span>Identifiant: {id}</span>
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

function Skills() {
  const [skillsData, setSkillsData] = useState([]);
  const PAGE_SIZE = 3;
  const [activePage, setActivePage] = useState(1);

  useEffect(() => {
    fetch("http://localhost:8081/skills")
      .then((response) => response.json())
      .then((data) => setSkillsData(data))
      .catch((error) => console.error("Error fetching skills data: ", error));
  }, []);

  const totalPages = Math.ceil(skillsData.length / PAGE_SIZE);

  const handleClick = (event) => {
    setActivePage(Number(event.target.text));
  };

  const pages = [];

  for (let number = 1; number <= totalPages; number++) {
    pages.push(
      <Pagination.Item
        key={number}
        active={number === activePage}
        onClick={handleClick}
      >
        {number}
      </Pagination.Item>
    );
  }

  const startIndex = (activePage - 1) * PAGE_SIZE;
  const endIndex = activePage * PAGE_SIZE;
  const currentSkills = skillsData.slice(startIndex, endIndex);

  return (
    <Card>
      <Row className="justify-content-center my-3">
        {currentSkills.map((skill, index) => (
          <Col key={index} xs={12} sm={6} md={4} lg={4}>
            <SkillCard
              logo={download.png}
              title={skill.name}
              issuer="Issuer Name" // You can set the issuer dynamically if available
              date="Date" // You can set the date dynamically if available
              id={skill.id}
            />
          </Col>
        ))}
        <Row className="my-3">
          <Col>
            <Pagination className="justify-content-center">{pages}</Pagination>
          </Col>
        </Row>
      </Row>
    </Card>
  );
}

export default Skills;
