import React, { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import axios from 'axios';

const ProjectCard = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:8082/projects');
        setProjects(response.data);
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  return (
    <div className="project-container">
      {projects.map((project, index) => (
        <Card key={index} className="project-card">
          <Card.Body>
            <Card.Title className="project-title">{project.title}</Card.Title>
            <Card.Text className="project-description">{project.description}</Card.Text>
            <Card.Text className="project-skills"><p1 >Technologies:</p1> {project.skills.join(', ')}</Card.Text>
            <Card.Link href={`http://localhost:8082/projects/${project.id}/skills`} className="project-link">
              Voir les comp√©tences
            </Card.Link>
          </Card.Body>
        </Card>
      ))}
    </div>
  );
};

export default ProjectCard;
