import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Pagination from 'react-bootstrap/Pagination';


function SkillCard(props) {
  const { name, level } = props;

  return (
    <Card className="shadow bg-light mb-4 animated bounceIn">
      <Card.Body>
        <Card.Title>{name}</Card.Title>
        <Card.Text>
          <span>Level: {level}</span>
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

function Skills() {
  const [skills, setSkills] = useState([]);
  const PAGE_SIZE = 3;
  const [activePage, setActivePage] = useState(1);

  useEffect(() => {
    const fetchSkills = async () => {
      try {
        const response = await axios.get('http://localhost:8081/skills');
        console.log('Skills data:', response.data);
        setSkills(response.data);
      } catch (error) {
        if (error.response) {
          // The request was made and the server responded with a status code
          console.error('Error fetching skills:', error.response.status, error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          console.error('Error fetching skills: No response received');
        } else {
          // Something happened in setting up the request that triggered an Error
          console.error('Error fetching skills:', error.message);
        }
      }
    };
    
  
    fetchSkills();
  }, []);
   // Empty dependency array ensures the effect runs only once (on mount)

  const totalPages = Math.ceil(skills.length / PAGE_SIZE);

  const handleClick = (event) => {
    setActivePage(Number(event.target.text));
  };

  const pages = [];

  for (let number = 1; number <= totalPages; number++) {
    pages.push(
      <Pagination.Item
        key={number}
        active={number === activePage}
        onClick={handleClick}
      >
        {number}
      </Pagination.Item>
    );
  }

  const startIndex = (activePage - 1) * PAGE_SIZE;
  const endIndex = activePage * PAGE_SIZE;
  const currentSkills = skills.slice(startIndex, endIndex);

  return (
    <Card>
      <Row className="justify-content-center my-3">
        {currentSkills.map((skill, index) => (
          <Col key={index} xs={12} sm={6} md={4} lg={4}>
            <SkillCard {...skill} />
          </Col>
        ))}
        <Row className="my-3">
          <Col>
            <Pagination className="justify-content-center">{pages}</Pagination>
          </Col>
        </Row>
      </Row>
    </Card>
  );
}

export default Skills;
