import React from "react";
import { Route, BrowserRouter as Router, Routes } from "react-router-dom";
import { Link as ScrollLink } from "react-scroll";
import { animateScroll as scroll } from "react-scroll";
import "./App.css";
import { Card } from "primereact/card";
import Sidebar from "./components/Sidebar";
import Certifications from "./pages/Certifications";
import Contact from "./pages/Contact";
import Footer from "./components/Footer";
import ContactForm from "./components/ContactForm";
import Experiences from "./pages/Experiences";
import Formations from "./pages/Formations";
import Projects from "./pages/Projects";
import Nav from "./components/Nav";

function App() {
  const scrollToTop = () => {
    scroll.scrollToTop();
  };

  return (
    <Router>
      <div className="background-image">
        <div className="container mb-5">
          {/* Navbar and Sidebar in the same row */}
          <div className="row">
            {/* Sidebar */}
            <div className="col-lg-3 col-md-4 mt-dynamic rp-md-0">
              <Sidebar />
              <div className="hidden-md w-100">
                <ContactForm />
              </div>
            </div>
            {/* Navbar */}
            <div className="col-lg-9 col-md-8 rp-md-0">
              <div className="glass-card bg-white mt-dynamic" id="res-nav">
                <Nav />
              </div>

              {/* Content area */}
              <div className="glass-card mt-4 content bg-white rm-md-0">
                <Routes>
                  <Route path="/" element={<Home scrollToTop={scrollToTop} />} />
                  <Route path="/Formations" element={<Formations />} />
                  <Route path="/Experiences" element={<Experiences />} />
                  <Route path="/Projects" element={<Projects />} />
                  <Route path="/Certifications" element={<Certifications />} />
                  <Route path="/Contact" element={<Contact />} />
                </Routes>
              </div>
            </div>

            <Footer />
          </div>
        </div>
      </div>
    </Router>
  );
}

function Home({ scrollToTop }) {
  // ...

  return (
    <section className="section">
      <Card title="Loubna GHAZALI">
        {/* ... */}
      </Card>
      <ScrollLink
        to="formations"  // L'identifiant de l'élément vers lequel vous souhaitez faire défiler
        smooth={true}
        duration={500}
      >
        Scroll to Formations
      </ScrollLink>
      <button onClick={scrollToTop}>Scroll to Top</button>
    </section>
  );
}

export default App;
