import React, { useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import axios from "axios";

function Formations() {
  const [formations, setFormations] = useState([]);
  const cardStyle = {
    backgroundColor: "#77b5ae", // Alternating background colors
  };

  useEffect(() => {
    axios.get("http://localhost:8084/educations")
      .then(response => {
        setFormations(response.data);
      })
      .catch(error => {
        console.error("Error fetching educations:", error);
      });
  }, []);

  return (
    <Card className="p-3" style={cardStyle}>
      <section className="section">
        {formations.map((formation, index) => (
          <Card key={index} className="mb-3">
            <Card.Body>
              <Card.Title>{formation.diplome}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">
                {formation.etablissement}
              </Card.Subtitle>
              <Card.Text>{formation.annees}</Card.Text>
            </Card.Body>
          </Card>
        ))}
      </section>
    </Card>
  );
}

export default Formations;
